CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

START TRANSACTION;


DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE TABLE "Category" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Name" character varying(250) NOT NULL,
        "Description" text NULL,
        "Slug" text NOT NULL,
        CONSTRAINT "PK_Category" PRIMARY KEY ("Id")
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE TABLE "Product" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Name" character varying(250) NOT NULL,
        "Description" text NULL,
        "Slug" text NOT NULL,
        "CategoryId" integer NOT NULL,
        CONSTRAINT "PK_Product" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_Product_Category" FOREIGN KEY ("CategoryId") REFERENCES "Category" ("Id") ON DELETE CASCADE
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE TABLE "ProductVariant" (
        "Id" integer GENERATED BY DEFAULT AS IDENTITY,
        "Name" character varying(250) NOT NULL,
        "Description" text NULL,
        "Slug" text NOT NULL,
        "ProductId" integer NOT NULL,
        CONSTRAINT "PK_ProductVariant" PRIMARY KEY ("Id"),
        CONSTRAINT "FK_ProductVariant_Product" FOREIGN KEY ("ProductId") REFERENCES "Product" ("Id") ON DELETE CASCADE
    );
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE UNIQUE INDEX "IX_Category_Slug" ON "Category" ("Slug");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE INDEX "IX_Product_CategoryId" ON "Product" ("CategoryId");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE UNIQUE INDEX "IX_Product_Slug" ON "Product" ("Slug");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE INDEX "IX_ProductVariant_ProductId" ON "ProductVariant" ("ProductId");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    CREATE UNIQUE INDEX "IX_ProductVariant_Slug" ON "ProductVariant" ("Slug");
    END IF;
END $EF$;

DO $EF$
BEGIN
    IF NOT EXISTS(SELECT 1 FROM "__EFMigrationsHistory" WHERE "MigrationId" = '20220704083951_init_product') THEN
    INSERT INTO "__EFMigrationsHistory" ("MigrationId", "ProductVersion")
    VALUES ('20220704083951_init_product', '6.0.6');
    END IF;
END $EF$;
COMMIT;

